fout=open('Resultado da análise.txt','w')# Definição de funçõesdef verificaAGCONTA(k,a,b,banco):    if a==b==0:        num = k        N = num[:-1]        S = 0        for i in range(1,len(N)+1):            m = int(N[-i])            S = S + m*(i+1)        d = 11 - S%11        if banco == '001':            if d == 10:                d = 'X'            elif d == 11:                d = '0'        N=str(d)        if N != num[-1]:            print('Agência/Conta inválida')    elif a!=b:        num = L[k][a-1:b]        N = num[:-1]        S = 0        for i in range(1,len(N)+1):            m = int(N[-i])            S = S + m*(i+1)        d = 11 - S%11        if banco == '001':            if d == 10:                d = 'X'            elif d == 11:                d = '0'        N=str(d)        if N != num[-1]:            fout.write('Posições %d a %d: Valor esperado - %s; Valor encontrado - %s\n'%(a,b,num[:-1]+str(d),num))def verificaCNPJ(k,a,b):    if a==b==0:        CNPJ = k        N=CNPJ[:-2]        for k in range(0,2):            S = 0            for i in range(1,9):                m = int(N[-i])                S = S + m*(i+1)            if len(N)>8:                for j in range(1,len(N)-7):                    m = int(N[-j-8])                    S = S + m*(j+1)            d = 11 - S%11            if d == 10:                d = 0            elif d == 11:                d = 0            N = N + str(d)        if N[-2:] != CNPJ[-2:]:            print('CNPJ inválido')    elif a!=b:        CNPJ = L[k][b-14:b]        N=CNPJ[:-2]        if N.isdigit()==False:            fout.write('Posições %d a %d: Valor esperado - Valores numéricos; Valor encontrado - %s\n'%(b-13,b,CNPJ))        else:            for k in range(0,2):                S = 0                for i in range(1,9):                    m = int(N[-i])                    S = S + m*(i+1)                if len(N)>8:                    for j in range(1,len(N)-7):                        m = int(N[-j-8])                        S = S + m*(j+1)                d = 11 - S%11                if d == 10:                    d = 0                elif d == 11:                    d = 0                N = N + str(d)            if N[-2:] != CNPJ[-2:]:                fout.write('Posições %d a %d: Valor esperado - %s; Valor encontrado - %s\n'%(b-13,b,N,CNPJ))def verificaCPF(k,a,b):    if a==b==0:        CPF = k        N = CPF[:-2]        S = 0        for i in range(1,10):            m = int(N[i-1])            S = S + m*(i)        d = S%11        if d == 10:            d = 0        elif d == 11:            d = 0        N = N + str(d)        S=0        for i in range(0,10):            m = int(N[i])            S = S + m*(i)        d = S%11        if d == 10:            d = 0        elif d == 11:            d = 0        N = N + str(d)        if N!=CPF:            print('CPF inválido')    if a!=b:        CPF = L[k][b-11:b]        N = CPF[:-2]        S = 0        if N.isdigit()==False:            fout.write('Posições %d a %d: Valor esperado - Valores numéricos; Valor encontrado - %s\n'%(b-10,b,CPF))        else:            for i in range(1,10):                m = int(N[i-1])                S = S + m*(i)            d = S%11            if d == 10:                d = 0            elif d == 11:                d = 0            N = N + str(d)            S=0            for i in range(0,10):                m = int(N[i])                S = S + m*(i)            d = S%11            if d == 10:                d = 0            elif d == 11:                d = 0            N = N + str(d)            if N!=CPF:                fout.write('Posições %d a %d: Valor esperado - %s; Valor encontrado - %s\n'%(b-10,b,N,CPF))def compara(k,a,b,J):    if len(J)==1:        if a<b and L[k][a-1:b] not in J:            fout.write('Posições %d a %d: Valor esperado -'%(a,b)+ str(J) +'; Valor encontrado - %s\n'%L[k][a-1:b])        elif a==b and L[k][a-1] not in J:            fout.write('Posição %d: Valor esperado - '%a + str(J) + '; Valor encontrado - %s\n'%L[k][a-1:b])    elif len(J)>1:        if a<b and L[k][a-1:b] not in J:            fout.write('Posições %d a %d: Valores esperados -'%(a,b)+ str(J) +'; Valor encontrado - %s\n'%L[k][a-1:b])        elif a==b and L[k][a-1] not in J:            fout.write('Posição %d: Valores esperados - '%a + str(J) +'; Valor encontrado - %s\n'%L[k][a-1:b])def verificaTipo(k,a,b,tipo):    if tipo=='digit':        if L[k][a-1:b].isdigit()==False:            fout.write('Posições %d a %d: Valores esperados - valores numéricos; Valores encontrados - %s\n'%(a,b,L[k][a-1:b]))    if tipo=='name':        if L[k][a-1:b]==' '*len([k][a-1:b]):                fout.write('Posições %d a %d: Valores esperados - valores alfanuméricos; Valores encontrados - %s\n'%(a,b,L[k][a-1:b]))def verificaTipoDigit(k,a,b):    if L[k][a-1:b].isdigit()==True:        return True    else:        return False    def verificaNome(k,a,b,nome):        if nome.upper() not in L[k][a-1:b].upper():            fout.write('Posições %d a %d: Valor esperado - %s; Valor encontrado - %s\n'%(a,b,nome,L[k][a-1:b]))def verificaData(k,a,b):    if L[k][a+1:a+3] not in ['01','02','03','04','05','06','07','08','09','10','11','12']:        fout.write('Posições %d a %d: Valores esperados - [01,02,03,04,05,06,07,08,09,10,11,12]; Valor encontrado - %s\n'%(a+2,a+3,L[k][a+1:a+3]))    elif L[k][a+1:a+3] in ['01','03','05','07','08','10','12'] and (int(L[k][a-1:a+1])>31 or int(L[k][a-1:a+1])==0):        fout.write('Posições %d a %d: Valores esperados - entre 01 e 31; Valor encontrado - %s\n'%(a-1,a,L[k][a-1:a+1]))    elif L[k][a+1:a+3] in ['02','04','06','09','11'] and (int(L[k][a-1:a+1])>30 or int(L[k][a-1:a+1])==0):        fout.write('Posições %d a %d: Valores esperados - entre 01 e 30; Valor encontrado - %s\n'%(a-1,a,L[k][a-1:a+1]))def verificaHora(k,a,b):    if int(L[k][a-1:a+1])>24:        fout.write('Posições %d a %d: Valores esperados - entre 00 e 24; Valor encontrado - %s\n'%(a,a+1,L[k][a-1:a+1]))    if int(L[k][a+1:a+3])>59:        fout.write('Posições %d a %d: Valores esperados - entre 00 e 59; Valor encontrado - %s\n'%(a+2,a+3,L[k][a+1:a+3]))    if int(L[k][a+3:a+5])>59:        fout.write('Posições %d a %d: Valores esperados - entre 00 e 59; Valor encontrado - %s\n'%(a+4,a+5,L[k][a+3:a+5]))def verificaCodBarras(k,a,b):    A=L[k][a-1:b]    B = A[0:4] + '0' + A[5:44]    S = int(B[0])*4 + int(B[1])*3 +int(B[2])*2 + int(B[3])*9    for i in range(13,53,8):        for j in range(9,1,-1):            S = S + int(B[i-j])*j    d = 11- S%11    if d==0 or d==10 or d==11:        d = 1    else:        d = d    B = A[0:4] + str(d) + A[5:44]    if str(d) != A[4]:        fout.write('Posições %d a %d: Valor esperado: %s - Valor encontrado: %s\n'%(a,b,B,A))def verificaCART(k):    if k not in ['11','12','17','31','51']:        fout.write('Carteira inválida. Utilize, 11,12,17,31 ou 51\n')def verificaTamanho(k,a):    if len(k)!= a:        fout.write('Formato invalido - o registro deve conter %d posições\n'%(a-1))# Instruções iniciais ao usuárioprint('Renomeie o arquivo a ser analisado com o titulo "CNAB240.txt", e o mantenha na mesma pasta deste programa.\nFavor preencher os dados abaixo para validação.\n')#Declaração de variáveiscontHL=0contDetalheABT=0contDetalheJT=0contDetalhePQT=0SERV = input('Serviço (1 - Pagamentos; 2 - Cobrança): ')CNPJint = int(input('CNPJ: '))verificaCNPJ(str(CNPJint).zfill(14),0,0)CNPJstr = str(CNPJint).zfill(14)CPFint = int(input('CPF: '))verificaCPF(str(CPFint).zfill(11),0,0)CPFstr = str(CPFint).zfill(14)AGstr = input('Agência: ').zfill(6).upper()verificaAGCONTA(AGstr,0,0,'001')CONTAstr = input('Conta: ').zfill(13).upper()verificaAGCONTA(CONTAstr,0,0,'001')CONVint = int(input('Convênio: '))CONVstr = str(CONVint).zfill(9)NOME_CONV = input('Nome: ').upper()LOGR_CONV = input('Logradouro: ').upper()NUM_CONVint = int(input('Número: '))NUM_CONVstr = str(NUM_CONVint).zfill(5)COMP_CONV = input('Complemento: ').upper()CID_CONV = input('Cidade: ').upper()CEP_CONVint = int(input('CEP: '))CEP_CONVstr = str(CEP_CONVint)EST_CONV = input('Estado: ').upper()if SERV=='2':    CARTint = int(input('Carteira: '))    CARTstr = str(CARTint)    verificaCART(CARTstr)    VARint = int(input('Variação: '))    VARstr = str(VARint).zfill(3)    verificaAGCONTA(VARstr,0,0,0)    MOD = int(input('Modalidade (1 - Simples; 2 - Vinculada; 3 - Descontada: '))    TIPO_CONV = int(input('Tipo de convênio (1,2,3,4 ou 5): '))print('\n')#Importação e leitura do arquivoL=open('CNAB240.txt', 'r').readlines()#Início da validaçãofor x in range (0,len(L)):    #Registro Header de Arquivo    if L[x][7] =='0':        HA=x        fout.write('Registro Header de Arquivo:\n')        verificaTamanho(L[x],241)        compara(x,1,3,'001')        compara(x,4,7,'0000')        compara(x,9,17,'         ')        compara(x,18,18,['1','2'])        if L[x][17]=='1' and L[x][18:32]!= CPFstr:            compara(x,19,32,CPFstr)        if L[x][17]=='2' and L[x][18:32]!= CNPJstr:            compara(x,19,32,CNPJstr)            compara(x,33,41,CONVstr)            compara(x,51,52,'  ')        if SERV=='1':            compara(x,42,45,'0126')            compara(x,46,50,'     ')        if SERV=='2':            compara(x,42,45,'0014')            compara(x,46,47,CARTstr)            compara(x,48,50,VARstr)        compara(x,53,57,AGstr[0:5])        compara(x,58,58,AGstr[5].upper())        compara(x,59,70,CONTAstr[0:12])        compara(x,71,71,CONTAstr[12].upper())        verificaNome(x,73,102,NOME_CONV.ljust(30))        compara(x,103,132,['BANCO DO BRASIL S.A.          ','BANCO DO BRASIL               ', 'BANCO DO BRASIL S/A           '])        compara(x,133,142,'          ')        compara(x,143,143,'1')        verificaData(x,144,151)